// Processamento otimizado para Milho
if ($input.first() === undefined) {
  return [{ json: { 
    erro: 'Nenhum dado recebido do nÃ³ anterior.', 
    timestamp: new Date().toISOString() 
  } }];
}

let precoExtraido = $input.first().json.preco;

if (precoExtraido === undefined || precoExtraido === null || precoExtraido === '') {
  return [{ json: { 
    ativo: 'milho',
    erro: 'PreÃ§o nÃ£o encontrado',
    timestamp: new Date().toISOString(),
    status: 'erro'
  } }];
}

try {
  let precoLimpo = precoExtraido
    .replace(/\s/g, '')
    .replace(/[R$]/g, '')
    .replace(/\./g, '')
    .replace(/,/g, '.');

  let precoNumerico = parseFloat(precoLimpo);

  if (isNaN(precoNumerico)) {
    throw new Error('ConversÃ£o para nÃºmero falhou: ' + precoExtraido);
  }

  const currentDate = new Date().toISOString().split('T')[0];
  const [year, month, day] = currentDate.split('-');
  const brazilianDate = `${day}/${month}/${year}`;

  return [{ json: {
    ativo: 'milho',
    data: brazilianDate,
    ultimo: precoNumerico,
    abertura: precoNumerico,
    maxima: precoNumerico,
    minima: precoNumerico,
    volume: null,
    variacao_pct: null,
    moeda: 'BRL',
    fonte: 'Investing.com',
    timestamp: new Date().toISOString(),
    status: 'sucesso'
  } }];
} catch (error) {
  return [{ json: {
